package com.qihoo.example.picture;import java.io.File;import com.qihoo.example.R;import android.app.Activity;import android.graphics.Bitmap;import android.graphics.BitmapFactory;import android.os.Bundle;import android.view.MotionEvent;import android.widget.ImageView;public class FullScreenPictureActivity extends Activity {		private int position = 0;	private File[] picts = null;		private ImageView imageView = null;		private double slide_prev_x = 0;	private double slide_prev_y = 0;		@Override	public void onCreate(Bundle savedInstanceState) {		super.onCreate(savedInstanceState);		setContentView(R.layout.fullpict);				PictureAndPosition p = getIntent().getParcelableExtra("picture");		this.position = p.getPostion();		this.picts = p.getFiles();				imageView = (ImageView) findViewById(R.id.full_imageView);		Bitmap bm = BitmapFactory.decodeFile(picts[position].getAbsolutePath());		imageView.setImageBitmap(bm);		imageView.setImageBitmap(bm);	}		@Override    public boolean onTouchEvent(MotionEvent event) {    	    	int iAction = event.getAction();    	double slide_curr_x = 0, slide_curr_y = 0;    	    	if (iAction == MotionEvent.ACTION_DOWN) {    		slide_prev_x = event.getX();    		slide_prev_y = event.getY();    	}    	    	if (iAction == MotionEvent.ACTION_UP) {    		slide_curr_x = event.getX();    		slide_curr_y = event.getY();    		    		if (slide_curr_x < slide_prev_x) {    			if(position == 0) {    				position = picts.length-1;    			} else {    				position = position -1;    			}    		} else if (slide_curr_x > slide_prev_x) {    			position = (position+1)%(picts.length);    		}    		    		Bitmap bm = BitmapFactory.decodeFile(picts[position].getAbsolutePath());    		imageView.setImageBitmap(bm);    		imageView.setImageBitmap(bm);    	}    	    	return super.onTouchEvent(event);    }}