package com.qihoo.example.picture;import java.io.File;import android.os.Parcel;import android.os.Parcelable;public class PictureAndPosition implements Parcelable {	private int position = 0;	private File[] files = null;		public PictureAndPosition(int position, File[] files) {		this.position = position;		this.files = files;	}		public int describeContents() {		return 0;	}	public void writeToParcel(Parcel dest, int flags) {		dest.writeArray(files);		dest.writeInt(position);	}		public static final Parcelable.Creator<PictureAndPosition> CREATOR = new Creator<PictureAndPosition>() {		public PictureAndPosition createFromParcel(Parcel source) {			Object[] objs = source.readArray(this.getClass().getClassLoader());			File[] files = new File[objs.length];			for (int i=0; i<objs.length; i++) {				files[i] = (File) objs[i];			}			int position = source.readInt();			PictureAndPosition p = new PictureAndPosition(position, files);			return p;		}		public PictureAndPosition[] newArray(int size) {			return new PictureAndPosition[size];		}			};		public File[] getFiles() {		return files;	}		public int getPostion() {		return position;	}}