package com.qihoo.feiyang.picture;import android.os.Bundle;import android.os.Handler;import android.os.Message;public class ThumbHandler extends Handler {		private AddableAdapter adapter = null;		public ThumbHandler(AddableAdapter adapter) {		this.adapter = adapter;	}		@Override	public void handleMessage(Message msg) {		System.out.println("ThumbHandler Receiver Message");		Bundle data = msg.getData();		adapter.addPicture(data);	}		}